import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChalkboard, faChalkboardTeacher, faFileArrowDown, faPerson, faRightFromBracket, faUser, faUserGroup, faUsers } from "@fortawesome/free-solid-svg-icons";
import {useNavigate} from 'react-router-dom'


const NavBar = (props) => {

    const navigate= useNavigate()

    const CustomLink=(props)=> {
        const path = window.location.pathname;
        return (
            <li className={props.visibility==='visible' ? '' : 'hide'}>
                <a draggable='false' className={path===props.href ? `text-decoration-none fs-5` : " text-decoration-none fs-5"} onClick={()=>navigate(props.href)}>
                    <FontAwesomeIcon icon={props.icon} classname="i"/>
                    <span>{props.span}</span>
                </a>
            </li>
        )
    }

    return (
        <div id="sideBar" className={`sideBar user-select-none no-drag ${props.className}`}>
            <div className="wrapper d-flex">
                <ul className="list-unstyled">

                    <CustomLink visibility='visible' href='/' icon={faChalkboard} name='home' span='Clasrooms'></CustomLink>
                    <CustomLink visibility='visible'  href='/' icon={faChalkboard} name='home' span='Teachers'></CustomLink>
                    <CustomLink visibility='visible'  href='/' icon={faChalkboard} name='home' span='Students'></CustomLink>
                    <CustomLink visibility='visible'  href='/' icon={faChalkboard} name='home' span='Parents'></CustomLink>
                    <CustomLink visibility='visible'  href='/' icon={faChalkboard} name='home' span='Files'></CustomLink>
                    <div className="d-flex flex-column-reverse">
                        <CustomLink visibility='not visible'  href='/' icon={faChalkboard} name='home' span='Administrators'></CustomLink>
                        <CustomLink visibility='not visible'  href='/' icon={faChalkboard} name='home' span='Educational Center'></CustomLink>
                        <CustomLink visibility='visible'  href='/' icon={faChalkboard} name='home' span='Management'></CustomLink>
                    </div>
                    <CustomLink href='visible' icon={faChalkboard} name='home' span='Log out'></CustomLink>
                </ul>
            </div> 
        </div>
    )
}

export default NavBar
